var duration;

export function getDuration(apikey, A, B) {
  var origin = A;
  var destination = B;
  var proxyUrl = 'https://cors-anywhere.herokuapp.com/';
  var url = proxyUrl + 'https://maps.googleapis.com/maps/api/distancematrix/json?origins=' 
    + origin + '&destinations=' + destination + '&key=' + apikey;

    fetch(url)
      .then((response) => response.json())
      .then(function(myJson) {
        duration = myJson.rows[0].elements[0].duration.value;
        duration = checkVar(duration);
        //duration = myJson.rows[0].elements[0].duration.value;
        //duration = checkVar(duration);
        console.log("duration is " + duration);
        return duration;
      });
    return duration;            
}

export function checkVar(dist){

  if(typeof dist !== 'undefined'){
      console.log("we in this");
      return duration;
  }
  else{
    setTimeout(checkVar(dist), 100);
    //setTimeout(function(){ alert("called it");}, 3000);
  }
}
