import React, { Component } from 'react'
import { ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import { InputGroup, InputGroupAddon, InputGroupText, Input } from 'reactstrap';

export class ButtonToggle extends Component {
    constructor(props) {
        super(props);

        // this.toggle = this.toggle.bind(this);
        this.state = {
          dropdownOpen: false,
          msg: props.message,
          numItems : 4,
          currentClick: 0,
          on: false,
          on2: false,
          on3: false,
          on4: false,
        };
      }

    toggle = () =>  {

      this.setState({
          dropdownOpen: !this.state.dropdownOpen
        });
      }

    toggleButton1 = () => {
        this.setState({
            on: !this.state.on,
            currentClick: 1,
        });
    }

    toggleButton2 = () => {
        this.setState({
            on: !this.state.on,
            currentClick: 1,
        });
    }

    toggleButton3 = () => {
        this.setState({
            on: !this.state.on,
            currentClick: 1,
        });
    }

    toggleButton4 = () => {
        this.setState({
            on: !this.state.on,
            currentClick: 1,
        });
    }

  render() {
      const {message} = this.props
      console.log(this.props)
    return (
      <div>
        <p>Please choose the amount of address:</p>
        <ButtonDropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
        <DropdownToggle caret>
          {message}
        </DropdownToggle>
        <DropdownMenu>
            {
                Array(this.state.numItems).fill(0).map( (_, i) => <DropdownItem key={i} onClick={this.toggleButton + (i+1)} >{i + 1 }</DropdownItem>)
            }
          {/* <DropdownItem onClick ={this}>1</DropdownItem>
          <DropdownItem>2</DropdownItem>
          <DropdownItem>3</DropdownItem>
          <DropdownItem>4</DropdownItem> */}
        </DropdownMenu>
      </ButtonDropdown>
         <div>
               {
                   Array(this.state.currentClick).fill(0).map( (_,x) => this.state.on && <Input placeholder="Address"></Input>)
               } 
        </div>
      </div>
    )
  }
}
