import React, { Component } from 'react'
import { ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import { InputGroup, InputGroupAddon, InputGroupText, Input } from 'reactstrap';

export class ButtonToggle extends Component {
    constructor(props) {
        super(props);

        // this.toggle = this.toggle.bind(this);
        this.state = {
          dropdownOpen: false,
          msg: props.message,
          numItems : 4,
          on1: false,
        };
      }

    toggle = () =>  {
      this.setState({
          dropdownOpen: !this.state.dropdownOpen
        });
      }

    toggle1 = () => {
        this.setState({
            on1: !this.state
        });
    }

  render() {
      const {message, message2} = this.props
      console.log(this.props)
    return (
      <div>
        <p>Please choose the amount of address:</p>
        <ButtonDropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
        <DropdownToggle caret>
          {message}
        </DropdownToggle>
        <DropdownMenu>
            {/* {
                Array(this.state.numItems).fill(0).map( (_, i) => <DropdownItem key={i} >{i + 1}</DropdownItem>)

            } */}
          <DropdownItem onClick={this.toggle1}>1</DropdownItem>
          <DropdownItem >2</DropdownItem>
          <DropdownItem>3</DropdownItem>
          <DropdownItem>4</DropdownItem>

        </DropdownMenu>
      </ButtonDropdown>
         <div>
          <InputGroup>
            <Input placeholder="Address"></Input>
          </InputGroup>
        </div>
      </div>
    )
  }
}
